DROP TABLE IF EXISTS `book_rent_tbl`;
DROP TABLE IF EXISTS `book_library_tbl`;
DROP TABLE IF EXISTS `book_rent_history_tbl`;

CREATE TABLE `book_rent_tbl` (
  `rent_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '대여 ID'
  `book_id` int(11) NOT NULL COMMENT '책 ID',
  `user_id` varchar(10) NOT NULL COMMENT '사번',
  `rent_date` date NOT NULL COMMENT '대여일',
  `due_date` date NOT NULL COMMENT '반납예정일',
  PRIMARY KEY (`rent_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='책 대여 현황 테이블';

CREATE TABLE `book_library_tbl` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '책 ID',
  `category_1` varchar(100) NOT NULL COMMENT '대분류',
  `category_2` varchar(100) NOT NULL COMMENT '중분류',
  `manage_no` varchar(100) NOT NULL COMMENT '관리번호',
  `book_name` varchar(100) NOT NULL COMMENT '도서명',
  `author` varchar(100) DEFAULT NULL COMMENT '저자',
  `publisher` varchar(100) DEFAULT NULL COMMENT '출판사',
  `publishing_date` date DEFAULT NULL COMMENT '출간일',
  `img_src` varchar(100) DEFAULT NULL COMMENT '도서 img',
  `isbn` varchar(100) DEFAULT NULL COMMENT 'isbn',
  PRIMARY KEY (`book_id`),
  UNIQUE KEY `book_id_UNIQUE` (`book_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='도서 목록 테이블';

CREATE TABLE `book_rent_history_tbl` (
  `history_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '이력 ID',
  `user_id` varchar(10) NOT NULL COMMENT '사번',
  `book_name` varchar(100) NOT NULL COMMENT '도서명',
  `author` varchar(100) NOT NULL COMMENT '저자',
  `publisher` varchar(100) NOT NULL COMMENT '출판사',
  `img_src` varchar(100) DEFAULT NULL COMMENT '도서 img',
  `manage_no` varchar(20) DEFAULT NULL COMMENT '관리번호',
  `state` int(11) NOT NULL COMMENT '대여상태 (1대여, 0반납)',
  `modify_date` date NOT NULL COMMENT '변경일',
  PRIMARY KEY (`history_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='대여이력 테이블';



